// src/components/PostDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';

const PostDetail = () => {
  const { id } = useParams();  // Obtenemos el id de la URL
  const [post, setPost] = useState(null);
  const [responses, setResponses] = useState([]);

  // Simulamos la carga de una publicación y sus respuestas
  useEffect(() => {
    const fetchedPost = {
      id,
      title: `Publicación ${id}`,
      content: `Contenido de la publicación ${id}`,
    };

    const fetchedResponses = [
      { id: 1, content: "Respuesta 1 a la publicación" },
      { id: 2, content: "Respuesta 2 a la publicación" },
    ];

    setPost(fetchedPost);
    setResponses(fetchedResponses);
  }, [id]);

  if (!post) {
    return <p>Cargando...</p>;
  }

  return (
    <div className="post-detail-container">
      <h2>{post.title}</h2>
      <p>{post.content}</p>

      <h3>Respuestas:</h3>
      <div className="responses-list">
        {responses.map((response) => (
          <div key={response.id} className="response">
            <p>{response.content}</p>
          </div>
        ))}
      </div>

      {/* Enlace para volver a la página principal */}
      <Link to="/home">Volver a las publicaciones</Link>
    </div>
  );
};

export default PostDetail;
