// src/components/Home.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  // Simulamos algunas publicaciones
  const posts = [
    { id: 1, title: "Publicación 1", content: "Contenido de la publicación 1" },
    { id: 2, title: "Publicación 2", content: "Contenido de la publicación 2" },
    { id: 3, title: "Publicación 3", content: "Contenido de la publicación 3" },
  ];

  return (
    <div className="home-container">
      <h2>Publicaciones</h2>
      <div className="post-list">
        {posts.map((post) => (
          <div className="post" key={post.id}>
            <h3>{post.title}</h3>
            <p>{post.content}</p>
            {/* Enlace para ver las respuestas a la publicación */}
            <Link to={`/post/${post.id}`}>Ver respuestas</Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
